import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as a,c as r,a as e,d as c,b as o,e as t}from"./app-b644cb9d.js";const n={},h={href:"https://javabetter.cn/zhishixingqiu/mianshi.html",target:"_blank",rel:"noopener noreferrer"},s=e("h2",{id:"_1-linux-常用命令",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#_1-linux-常用命令","aria-hidden":"true"},"#"),c(" 1. Linux 常用命令")],-1),u={href:"https://javabetter.cn/pdf/linux.html",target:"_blank",rel:"noopener noreferrer"},p=t('<p>我自己常用的 Linux 命令有 top 查看系统资源、ps 查看进程、netstat 查看网络连接、ping 测试网络连通性、find 查找文件、chmod 修改文件权限、kill 终止进程、df 查看磁盘空间、free 查看内存使用、service 启动服务、mkdir 创建目录、rm 删除文件、rmdir 删除目录、cp 复制文件、mv 移动文件、zip 压缩文件、unzip 解压文件等等这些。</p><blockquote><p>下面这些列表供大家作为参考，可以根据自己的实际情况进行选择。</p></blockquote><h3 id="文件操作" tabindex="-1"><a class="header-anchor" href="#文件操作" aria-hidden="true">#</a> 文件操作</h3><ul><li><code>ls</code>：列出目录内容。<code>ls -l</code>显示详细信息，<code>ls -a</code>显示隐藏文件。</li><li><code>cd</code>：更改当前目录。<code>cd ..</code>回到上级目录，<code>cd ~</code>回到用户的主目录。</li><li><code>pwd</code>：显示当前工作目录的完整路径。</li><li><code>cp</code>：复制文件或目录。<code>cp source_file target_file</code>复制文件，<code>cp -r source_directory target_directory</code>复制目录。</li><li><code>mv</code>：移动或重命名文件或目录。</li><li><code>rm</code>：删除文件或目录。<code>rm -r</code>递归删除目录及其内容。</li><li><code>mkdir</code>：创建新目录。</li><li><code>cat</code>：查看文件内容。<code>cat file1 file2</code>合并文件内容显示。</li></ul><h3 id="系统管理" tabindex="-1"><a class="header-anchor" href="#系统管理" aria-hidden="true">#</a> 系统管理</h3><ul><li><code>ps</code>：显示当前运行的进程。<code>ps aux</code>显示所有进程。</li><li><code>top</code>：实时显示进程动态。</li><li><code>kill</code>：终止进程。<code>kill -9 PID</code>强制终止。</li><li><code>df</code>：显示磁盘空间使用情况。<code>df -h</code>以易读格式显示。</li><li><code>du</code>：显示目录或文件的磁盘使用情况。</li><li><code>free</code>：显示内存和交换空间的使用情况。</li><li><code>chmod</code>：更改文件或目录的权限。</li><li><code>chown</code>：更改文件或目录的所有者和所属组。</li></ul><h3 id="网络管理" tabindex="-1"><a class="header-anchor" href="#网络管理" aria-hidden="true">#</a> 网络管理</h3><ul><li><code>ping</code>：检查与远程服务器的连接。</li><li><code>wget</code>：从网络上下载文件。</li></ul><h3 id="压缩和解压" tabindex="-1"><a class="header-anchor" href="#压缩和解压" aria-hidden="true">#</a> 压缩和解压</h3><ul><li><code>tar</code>：打包或解包<code>.tar</code>文件。<code>tar cvf archive.tar files</code>打包，<code>tar xvf archive.tar</code>解包。</li><li><code>gzip</code> / <code>gunzip</code>：压缩或解压<code>.gz</code>文件。</li><li><code>zip</code> / <code>unzip</code>：压缩或解压<code>.zip</code>文件。</li></ul><h3 id="查找文件" tabindex="-1"><a class="header-anchor" href="#查找文件" aria-hidden="true">#</a> 查找文件</h3><ul><li><code>find</code>：在目录树中查找文件。<code>find /directory/ -name filename</code>。</li></ul>',12);function f(_,m){const d=l("ExternalLinkIcon");return a(),r("div",null,[e("blockquote",null,[e("ol",null,[e("li",null,[e("a",h,[c("Java 面试指南（付费）"),o(d)]),c("收录的用友金融一面原题：Linux 的常用命令")])])]),s,e("p",null,[c("推荐阅读："),e("a",u,[c("常用高频 Linux 速查备忘手册"),o(d)])]),p])}const k=i(n,[["render",f],["__file","linux.html.vue"]]);export{k as default};
